def add(a: int, b: int) -> int:
    """Adds two integer numbers."""
    return a + b

/**
 * Calculates the sum of two numbers.
 * @param {number} x The first number.
 * @param {number} y The second number.
 * @returns {number} The sum of x and y.
 */
function sum(x, y) {
    return x + y;
}
